const Screen = (props) => {
    return (
        <div id="screen">{props.initial}</div>
    );
}

// Add event to write in screen when pressing keyboard key
window.addEventListener("keydown", (event) => {
    keyboardWrite(event.key);
});

const keyboardWrite = (key) => {        
    let screen = document.querySelector("#screen");
    let screenContent = screen.innerText;
    let operations = ["+, x, X, -, /"];
    
    // Write numbers
    if (!isNaN(parseInt(key)) && screenContent !== "0") {
        screen.innerText += key;
    } else if (!isNaN(parseInt(key))) { // Numbers, but in screen there is only a "0"
        screen.innerText = key;
    }

    // Write operations (+, -, x, /)
    else if (operations.find(key)) {
        console.log("")
    }
}

export default Screen;
