// Normal button component
const Button = (props) => {
    return (
        <input type="button" className="operation-button button" value={props.text} />
    )
}

// Side menu receiving string of operations to perform (+ , - , x, ...)
const SideMenu = (props) => {
    let operations = props.operations.split(',');
    let buttons = operations.map(operation => {
        return <Button text={operation} />;
    });

    return (
        <div className="side-menu calculator-section">
            {buttons}
        </div>
    );
}

export default SideMenu;
