// Normal button component
const Button = (props) => {
    return (
        <input type="button" className="operation-button button" value={props.text} onClick={props.click} />
    )
}

// Side menu receiving string of operations to perform (+ , - , x, ...)
const SideMenu = (props) => {   
    let operations = props.operations.split(',');
    let buttons = operations.map(operation => {
        return <Button text={operation} click={() => addOperation(operation)} />;
    });

    return (
        <div className="side-menu calculator-section">
            {buttons}
        </div>
    );
}

// Function to add the operation simbol to the screen
const addOperation = (operation) => {
    let screen = document.getElementById("screen");
    let screenContent = screen.innerText.split("");
    // Check if last element is a number (true) or not
    let lastNumber = (isNaN(parseInt(screenContent.at(-1)))) ? false : true;
    if (lastNumber) {
        if (screenContent.length === 1 && screenContent.at(-1) === "0")
        screen.innerText += " " + operation;
    } else {
        screenContent.pop();
        screenContent.push(operation);
        screen.innerText = screenContent.join("");
    }
}

export default SideMenu;
